from langflow.custom import Component
from langflow.inputs import MessageTextInput, BoolInput
from langflow.template import Output
from langflow.schema import Data
from youtube_transcript_api import YouTubeTranscriptApi, TranscriptsDisabled, NoTranscriptFound, VideoUnavailable
import re

class YouTubeTranscriptComponent(Component):
    display_name = "YouTube Transcript Fetcher"
    description = "Fetches transcripts from YouTube videos with optional timestamps."
    icon = "youtube"

    inputs = [
        MessageTextInput(
            name="youtube_id",
            display_name="YouTube Video ID",
            info="The ID of the YouTube video.",
        ),
        BoolInput(
            name="include_timestamps",
            display_name="Include Timestamps",
            info="Boolean to include timestamps in the transcript.",
        ),
    ]

    outputs = [
        Output(display_name="Transcript", name="transcript", method="fetch_transcript"),
    ]

    preferred_languages = [
        'en', 'es', 'fr', 'de', 'pt', 'ru', 'zh', 'nl', 'ja', 'ko', 'hi', 'tr', 
        'pl', 'sv', 'no', 'da', 'fi', 'el', 'th', 'vi'
    ]

    def fetch_transcript(self) -> Data:
        youtube_id = self.youtube_id
        include_timestamps = self.include_timestamps

        # Validate YouTube ID
        if not youtube_id:
            self.status = "YouTube ID is empty."
            return Data(data={"error": "YouTube ID is empty."})

        try:
            # Fetch the transcript in the first available preferred language
            transcript = self.get_transcript_in_preferred_language(youtube_id)

            if transcript is None:
                self.status = "No transcript found in preferred languages."
                return Data(data={"error": "No transcript found in preferred languages."})

            # Process the transcript
            processed_transcript = self.process_transcript(transcript, include_timestamps)

            self.status = "Transcript fetched successfully."
            return Data(data={"transcript": processed_transcript})

        except VideoUnavailable:
            self.status = "Video is unavailable."
            return Data(data={"error": "Video is unavailable."})
        except TranscriptsDisabled:
            self.status = "Transcripts are disabled for this video."
            return Data(data={"error": "Transcripts are disabled for this video."})
        except NoTranscriptFound:
            self.status = "No transcript found for this video."
            return Data(data={"error": "No transcript found for this video."})
        except Exception as e:
            self.status = f"An error occurred: {str(e)}"
            return Data(data={"error": f"An error occurred: {str(e)}"})

    def get_transcript_in_preferred_language(self, video_id: str):
        try:
            transcript_list = YouTubeTranscriptApi.list_transcripts(video_id)
            for lang in self.preferred_languages:
                if transcript_list.find_transcript([lang]):
                    return transcript_list.find_transcript([lang]).fetch()
        except Exception as e:
            self.status = f"An error occurred while fetching the transcript: {str(e)}"
            return None
        return None

    def process_transcript(self, transcript: list, include_timestamps: bool) -> str:
        if include_timestamps:
            return "\n".join([f"{self.format_timestamp(entry['start'])} - {entry['text']}" for entry in transcript])
        else:
            return "\n".join([entry['text'] for entry in transcript])

    def format_timestamp(self, start: float) -> str:
        hours, remainder = divmod(start, 3600)
        minutes, seconds = divmod(remainder, 60)
        if hours > 0:
            return f"{int(hours):02}:{int(minutes):02}:{int(seconds):02}"
        else:
            return f"{int(minutes):02}:{int(seconds):02}"

