from langflow.custom import Component
from langflow.inputs import MessageTextInput, SecretStrInput
from langflow.template import Output
from langflow.schema import Data
import requests

class YouTubeVideoInfoExtractor(Component):
    display_name = "YouTube Video Info Extractor"
    description = "Extracts the title and description of a YouTube video using its ID."
    icon = "youtube"

    inputs = [
        MessageTextInput(
            name="youtube_id",
            display_name="YouTube Video ID",
            info="The ID of the YouTube video.",
        ),
        SecretStrInput(
            name="api_key",
            display_name="YouTube API Key",
            info="Your YouTube Data API key.",
            advanced=False,
        )
    ]

    outputs = [
        Output(display_name="Video Info", name="video_info", method="fetch_video_info"),
    ]

    def fetch_video_info(self) -> Data:
        youtube_id = self.youtube_id.strip()
        api_key = self.api_key.strip()  # Use strip directly since SecretStrInput stores the value as string

        if not youtube_id:
            return Data(data={"error": "YouTube ID cannot be empty"})
        
        if not api_key:
            return Data(data={"error": "API Key cannot be empty"})

        try:
            api_url = f"https://www.googleapis.com/youtube/v3/videos?id={youtube_id}&part=snippet&key={api_key}"
            response = requests.get(api_url)
            response.raise_for_status()

            video_data = response.json()
            if not video_data["items"]:
                return Data(data={"error": "Invalid YouTube ID or video not found"})

            snippet = video_data["items"][0]["snippet"]
            title = snippet.get("title", "No Title")
            description = snippet.get("description", "")

            return Data(data={"title": title, "description": description})

        except requests.exceptions.RequestException as e:
            return Data(data={"error": str(e)})
        except KeyError as e:
            return Data(data={"error": f"Key error: {str(e)}"})
        except Exception as e:
            return Data(data={"error": f"An unexpected error occurred: {str(e)}"})

