from langflow.custom import Component
from langflow.inputs import MessageTextInput
from langflow.template import Output
from langflow.schema import Data
import re
import json

class JSONExtractorComponent(Component):
    display_name = "JSON Extractor"
    description = "Extracts and cleans a JSON object from a given string input."
    icon = "braces"

    inputs = [
        MessageTextInput(
            name="input_string",
            display_name="Input String",
            info="String containing the JSON object.",
        )
    ]

    outputs = [
        Output(display_name="Clean JSON String", name="clean_json_string", method="extract_and_clean_json"),
    ]

    def extract_and_clean_json(self) -> Data:
        input_text = self.input_string

        try:
            # Extract JSON object from the string
            json_match = re.search(r'\[.*\]', input_text, re.DOTALL)
            if not json_match:
                raise ValueError("No JSON object found in the input text.")

            json_str = json_match.group()
            json_str = json_str.strip()
            # Clean and validate the JSON object
            json_obj = json.loads(json_str)
            cleaned_json_str = json.dumps(json_obj, indent=2)
            self.status = Data(data={"text": cleaned_json_str})
            return Data(data={"text":cleaned_json_str})
        
        except json.JSONDecodeError as e:
            self.status = f"JSON decode error: {e}"
            return Data(data={"text": f"Error: Invalid JSON format - {e}"})
        
        except ValueError as e:
            self.status = str(e)
            return Data(data={"text": f"Error: {e}"})
        
        except Exception as e:
            self.status = f"Unexpected error: {e}"
            return Data(data={"text": f"Error: An unexpected error occurred - {e}"})

from langflow.custom import CustomComponent
from langflow.schema import Data


class MergeDataComponent(CustomComponent):
    display_name = "Merge Data"
    description = "Combines multiple data sources into a single unified Data object."
    beta: bool = True
    name = "MergeData"

    field_config = {
        "data": {"display_name": "Data"},
    }

    def build(self, data: list[Data]) -> Data:
        if not data:
            return Data()
        if len(data) == 1:
            return data[0]
        merged_data = Data()
        for value in data:
            if merged_data is None:
                merged_data = value
            else:
                merged_data += value
        self.status = merged_data
        return merged_data

